import React, { useState } from 'react'
//import './App.css';
import { UseFetch } from './components/UseFetch'
import { AllCards } from './components/cards/AllCards'
function App() {

  const [url, setUrl] = useState('https://pokeapi.co/api/v2/pokemon');
  const estado = UseFetch(url)
  const { cargando, data } = estado
  cargando ? console.log('cargando') : console.log("Resultados desde app.js:", data.results)

  return (
    <div>
      <h1>Pokedex</h1>
      {
        cargando
          ?
          <div class="spinner-border" role="status">
            <span class="sr-only">Loading...</span>
          </div>
          :
          <div>
            <AllCards results={data.results} />
            <div className='container m-auto'>
              <center>
                <button onClick={() => setUrl(data.previous)} className='button-54'>Anterior</button>
                <button onClick={() => setUrl(data.next)} className='button-54'>Siguiente</button>
              </center>
            </div>
          </div>
      }
    </div>
  );
}

export default App;
