import React, { useState } from 'react'
import { TypeScript } from './TypeScript'
import { UseFetch } from '../UseFetch'
import ReactCardFlip from 'react-card-flip'
import './Cards.css'
export const PokemonCard = ({ url }) => {
    const [isFlipped, setIsFlipped] = useState(false)
    var estado = UseFetch(url)
    //estado.data = fetch(url).then(res=>res.json()).then(res=>(console.log(res)))
    //console.log("Data del fetch en PokemonCard",estado)
    const { cargando, data } = estado
    const handleClick = e => {
        setIsFlipped(!isFlipped);
    }
    if (data != null) {
        return (
            <div>{
                cargando ?
                    <h1>Cargando...</h1>
                    :
                    <div className="flip-card">
                        <ReactCardFlip isFlipped={isFlipped} flipDirection="vertical" flipSpeedBackToFront={10} flipSpeedFrontToBack={10}>
                            <div className='card' style={{ width: '14rem', height: '14rem'}} onClick={handleClick}>
                            </div>
                            <div className='card' style={{ width: '14rem', height: '14rem'}} onClick={handleClick}>
                                <h1>Simon</h1>
                            </div>
                        </ReactCardFlip>
                    </div>



            }
            </div>
        )
    } else return 0;
}
