import React from 'react'

export const TypeScript = ({ typeArray }) => {
    let a = typeArray
    console.log(typeArray)
    if (typeArray.length > 1) {
        return (
            <td>{capitalize(typeArray[0].type.name)}/{capitalize(typeArray[1].type.name)}</td>
        )
    } else return (
        <td><img src={require('../assets/icons/bug.svg').default}></img></td>
    )
}

function capitalize(str){
    const lower = str.toLowerCase();
    return str.charAt(0).toUpperCase()+lower.slice(1)
}
