import React, { useState } from 'react'
import { TypeScript } from './TypeScript'
import { UseFetch } from '../UseFetch'
import ReactCardFlip from 'react-card-flip'
import './Cards.css'
export const PokemonCard = ({ url }) => {
    const [isFlipped, setIsFlipped] = useState(false)
    var estado = UseFetch(url)
    //estado.data = fetch(url).then(res=>res.json()).then(res=>(console.log(res)))
    //console.log("Data del fetch en PokemonCard",estado)
    const { cargando, data } = estado
    const handleClick = e => {
        setIsFlipped(!isFlipped);
    }
    if (data != null) {
        return (
            <div>{
                cargando ?
                    <h1>Cargando...</h1>
                    :
                    <div>
                        <div className='card-inner'>
                            <div className='flipcard-front'>
                                <div className="card" alt="front" onClick={handleClick}><h1>Titulo 1</h1></div>
                            </div>
                            <div className='flipcard-back'>
                                <div className="card" alt="back" onClick={handleClick}><h1>Titulo 2</h1></div>
                            </div>
                        </div>
                    </div>

            }
            </div>
        )
    } else return 0;
}
