import React from 'react'
import { UseFetch } from '../UseFetch'
import FlipCard from 'react-flipcard'


export const PokemonCard = ({ url }) => {
    console.log("URL Recibido por PokemonCard:", url)
    var estado = UseFetch(url)
    //estado.data = fetch(url).then(res=>res.json()).then(res=>(console.log(res)))
    //console.log("Data del fetch en PokemonCard",estado)
    const { cargando, data } = estado
    console.log("Valor de data:", data)
    //data = 
    //console.log("Resultados del data",data)
    if (data != null) {
        render();{
            return (
            <div>{
                cargando ?
                    <h1>Cargando...</h1>
                    :
                    <div className='card' style={{ width: '14rem' }}>
                        <FlipCard>
                            <div className="portada">
                                
                        <center>
                            <div className = 'card-header'>
                            <h1>#{data.id.toString().padStart(3, 0)}</h1>
                            </div>
                            <div className = 'card-body'>
                                <img src = {data.sprites.front_default} alt = 'pokemon'/>
                            </div>
                            <div className='card-footer'>
                                <p className='card-text text-capitalize'>{data.forms[0].name}</p>
                            </div>
                            </center>
                            </div>
                            <div className="contenido">

                            </div>
                            
                        </FlipCard>
                    </div>
            }
            </div>
        )};
    } else return 0;
};
